    {
      "id": "act_7",
      "name": "Physical Exam",
      "instanceType": "Activity",
      "description": "240 min. After screening, medical assessment only performed to include medical review and targeted examination, as appropriate.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_8",
      "name": "Height and Weight",
      "instanceType": "Activity",
      "description": "Height is at screening only. Weight on Day 1 will be collected before the start of the procedure to induce hypoglycemia.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_9",
      "name": "Collect Hypoglycemic Events",
      "instanceType": "Activity",
      "description": "Refer to Section 9.5.5.1.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_10",
      "name": "Collect Concomitant Medications",
      "instanceType": "Activity",
      "description": "Review patients’ insulin regimens to confirm acceptability to undergo the procedure to induce hypoglycemia.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_11",
      "name": "Meal",
      "instanceType": "Activity",
      "description": "After completion of all PK sampling, patients will be provided a carbohydrate-rich meal. The investigator will ensure the patient’s PG is stable. A prandial insulin dose will be administered if needed.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_12",
      "name": "Insulin infusion to Induce Hypoglycemia",
      "instanceType": "Activity",
      "description": "Procedures in Period 2 will occur at approximately the same time as those in Period 1. Insulin infusion is stopped once bedside PG is <60 mg/dL.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_13",
      "name": "Bedside PG Monitoring for Safety",
      "instanceType": "Activity",
      "description": "Samples will be collected during the hypoglycemia induction procedure and post study treatment (up to 120 mins). Bedside PG monitoring will be conducted no more than 10 minutes apart while bedside PG is ≥90 mg/dL and no more than 5 minutes apart when bedside PG is <90 mg/dL.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_14",
      "name": "Study Treatment Administration",
      "instanceType": "Activity",
      "description": "Once bedside PG reaches <60 mg/dL, the insulin infusion is stopped and approximately 5 minutes later study drug is administered. Indicates Time = 0 min.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_15",
      "name": "Injection-Site Assessment",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction, 90 min. Injection-site assessment for IMG only.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_16",
      "name": "Nasal Inspection",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction, 90 min. Nasal inspection both for IMG and LY900018.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_17",
      "name": "Vital Signs (Supine Blood Pressure, Pulse Rate, and Body Temperature)",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction, Predose, 15, 30, 60, 120, 240 min. Time points may be added for each period if warranted and agreed upon between Lilly and the investigator. Predose time point will be between insulin infusion stop and study treatment.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_18",
      "name": "Single 12-lead ECG (Local)",
      "instanceType": "Activity",
      "description": "",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_19",
      "name": "Triplicate 12-lead ECG (Central)",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction (30 and 15 min prior to insulin infusion), Predose, 15, 30, 60, 120, 240 min. Time points may be added for Period 1 and Period 2 if warranted and agreed upon between Lilly and the investigator. Predose time point will be between insulin infusion stop and study treatment.",
      "activityGroupId": "grp_1"
    },
    {
      "id": "act_20",
      "name": "Clinical Serology Tests",
      "instanceType": "Activity",
      "description": "See Appendix 2, Clinical Laboratory Tests, for details.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_21",
      "name": "Clinical Lab Tests (Hematology, Clinical Chemistry, and Urinalysis)",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction. See Appendix 2, Clinical Laboratory Tests, for details. Patients do not need to fast for samples at screening or follow-up. At Periods 1 and 2, samples should be collected from patients who have fasted at least 8 hours before any study procedures.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_22",
      "name": "HbA1c",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction. See Appendix 2, Clinical Laboratory Tests, for details.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_23",
      "name": "Pregnancy Test (Female patients of childbearing potential only)",
      "instanceType": "Activity",
      "description": "Serum pregnancy test will be performed at screening. Urine pregnancy test will be performed at every admission period and follow-up visit if applicable.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_24",
      "name": "FSH (Female patients only)",
      "instanceType": "Activity",
      "description": "When needed to confirm postmenopausal status.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_25",
      "name": "Ethanol Testing",
      "instanceType": "Activity",
      "description": "Additional tests can be done at the discretion of the investigator.",
