    },
    {
      "id": "act_26",
      "name": "Urine Drug Screen",
      "instanceType": "Activity",
      "description": "Additional tests can be done at the discretion of the investigator.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_27",
      "name": "PK (Glucagon)",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction, Predose, 5, 10, 15, 20, 25, 30, 40, 50, 60, 90, 120, 240 min. Sampling times are relative to the time of study treatment administration (0 min). Predose time point will be between insulin infusion stop and study treatment.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_28",
      "name": "Plasma Glucose for PD",
      "instanceType": "Activity",
      "description": "Pre-hypoglycemia induction, -5, Predose, 5, 10, 15, 20, 25, 30, 40, 50, 60, 90, 120, 240 min. -5 mins = stop insulin infusion. Sampling times are relative to the time of study treatment administration (0 min). Predose time point will be between insulin infusion stop and study treatment.",
      "activityGroupId": "grp_2"
    },
    {
      "id": "act_29",
